mapper.select:
  		select * from replylist
		where pnum = #{pnum}
		and rremoveyn = 'n'

		order by rnum desc
		limit #{startNum} , #{perPage}

mapper.update:
  		update replylist
		set
		restep = restep+1
		where pnum = #{pnum}
		and restep >= #{restep}

  		UPDATE replylist
		SET help = help +1
		WHERE rnum = #{rnum}

  		UPDATE replylist
		SET useless = useless +1
		WHERE rnum = #{rnum}

  		UPDATE replylist
		SET rremoveyn = 'y'
		WHERE rnum = #{rnum}
